<?php    if (isset($_SESSION['nbpages'])){		/*foreach ($tab as $film) {			echo "<a href='index.php?page=film&id=".$film['ID_FILM']."'><div class='film'>";			echo $film['TITRE']."<br/>";			echo "</div></a>";		}*/				/*---------mise en variable des sessions utilisés pour la pagination----------*/		if (!isset($_GET['numpage'])){			$num = 0;		} else {			$num = ($_GET['numpage']-1);		}		$tab = $_SESSION['tab'];		$nb_element_par_page = $_SESSION['elemparpage'];				/*---------affichage des elements---------*/		for ($i = ($num*$nb_element_par_page); $i<($num*$nb_element_par_page+$nb_element_par_page) && $i<count($tab);$i++){				echo "<a href='index.php?page=film&id=".$tab[$i]['ID_FILM']."'><div class='film'>";				echo $tab[$i]['TITRE']."<br/>";				echo "</div></a>";		}	}		/*--------affichage du nombre de pages---------*/		if ($_SESSION['nbpages'] > 1 ){		for ($i=1; $i<=$_SESSION['nbpages']; $i++){				include('vues/Vpagination.php');		}	}?>