<?phpinclude_once ('modeles/Mconnect.php');	//je récupère la recherche	$recherche=$_POST['recherche'];	$recherche=utf8_decode($recherche);		//je remplace les caractères spéciaux pour améliorer la recherche dans la BDD	$recherche = str_replace("àáâãäåòóôõöøèéêëçìíîïùúûüÿñ", "aaaaaaooooooeeeeciiiiuuuuyn", $recherche); 		//Connexion à la base 		$bd = new Mconnect();				$connect=$bd->getRessource();				$stmt = ociparse($connect,"select * from CLIENT where UPPER(NOM) LIKE UPPER('%$recherche%')"); 		//On parse la requête à effectuer sans oublier de lui passer la chaine de connexion en paramêtre 		ociexecute($stmt,OCI_DEFAULT); 		//On execute la requête en lui passant l'option OCI_DEFAULT 				$bd->close(); 				$tab = Mutilitaire::tableau($stmt);				foreach ($tab as $film) {			echo $film['NOM'], $film['PRENOM']."<br/>";		}?>