<script type="text/javascript">//fonction permettant d'envoyer des informations sur une autre page en AJAX en mode asynchronefunction sendData(param, page, id) {  if(document.all) {		//Internet Explorer		var XhrObj = new ActiveXObject("Microsoft.XMLHTTP") ;	}//fin if	else	{		//Mozilla		var XhrObj = new XMLHttpRequest();		// On précise le contenu de ce qui est renvoyé		if (XhrObj.overrideMimeType){			XhrObj.overrideMimeType('html/javascript');		}	}//fin else	//définition de l'endroit d'affichage:	var content = document.getElementById(id);		//Appel asynchrone	XhrObj.open("POST", page);	//Ok pour la page cible	XhrObj.onreadystatechange = function()	{		if (XhrObj.readyState == 4 && XhrObj.status == 200)			content.innerHTML = XhrObj.responseText ;	}	XhrObj.setRequestHeader('Content-Type','application/x-www-form-urlencoded');	XhrObj.send(param);		}//fonction permettant d'envoyer des informations sur une autre page en AJAX en mode synchronefunction sendDataSynchrone(param, page, id){  if(document.all)	{		//Internet Explorer		var XhrObj = new Active}//fin if	else	{		//Mozilla		var XhrObj = new XMLHttpRequest();		// On précise le contenu de ce qui est renvoyé		if (XhrObj.overrideMimeType){			XhrObj.overrideMimeType('html/javascript');		}	}//fin else	//définition de l'endroit d'affichage:	var content = document.getElementById(id);		//Appel synchrone	XhrObj.open("POST", page, false);	XhrObj.setRequestHeader('Content-Type','application/x-www-form-urlencoded');	XhrObj.send(param);	if (XhrObj.readyState == 4 && XhrObj.status == 200){		content.innerHTML = XhrObj.responseText ;	}}</script>	