var cookieVal = LireCookie('sondage');
if(cookieVal != null){//**** Les resultats du sondage ****
        $.ajax({
        url: "/ajaxsondage",
                data:"",
        type: "post",
        beforeSend: function(){
       },
        success: function(data){
         $("#reponseSondage").html(data);
                 $("#questionSondage").hide();
       }
      });
}else{//**** Les Question du sondage ****
        $.ajax({
        url: "/ajaxsondagequestions",
                data:"",
        type: "post",
        beforeSend: function(){
       },
        success: function(data){
         $("#questionSondage").html(data);
                 $("#reponseSondage").hide();
       }
      });
}
//Insertion des cookie
function ValidateForm(numSondage){
        var valueRadio = $('input[name=rep[]]:checked').val(); 
        var numeroSondage =numSondage;
        var numUser = LireCookie('idUser');
        if(numUser != null){
                id = numUser;
        }else{
                id= new Date().getTime();
        }
        if(typeof(valueRadio)!="undefined" && valueRadio != ''){
                var today = new Date();
                today.setDate(today.getDate()+1);
                today.setHours(0,1,0);
                EcrireCookie('sondage', numSondage, today, "/");
                EcrireCookie('idUser', id, today, "/");
                $.ajax({
                        url: "/ajaxsondage",
                        data: "reponse="+valueRadio+"&numSondage="+numeroSondage+"&idUser="+id,
                        type: "post",
                        beforeSend: function(){
                       },
                        success: function(data){
                         $("#reponseSondage").html(data);
                                 $("#reponseSondage").show();
                         $("#questionSondage").hide();
                       }
                      });
                }else{
                        return false;
                }
        }

