function submitForm() {
        var $email = $("#emailId");
        $validEmail = checkMailAddress($email.val());
        if($validEmail==true){
                return true;
        }else{
                $("#errorLabel").show();
                $email.focus();
                return false;
        }
}

var currentCharte = "CIL";
var histoStatus = "hide";
function toggleLieuHisto(status){
        if(status==1){
                histoStatus = "show";
        } else if(status==2){
                histoStatus = "hide";
        } else if(status==0){
                if(histoStatus=="show"){
                        histoStatus = "hide";
                } else {
                        histoStatus = "show";
                }
        }
        if(histoStatus=="hide"){
                jQuery("#toggleIcon").removeClass("navOpened");
                jQuery("#hiddenFavLie").hide();
                jQuery("#toutAfficher").show();
                jQuery("#reduire").hide();
        } else {
                jQuery("#toggleIcon").addClass("navOpened");
                jQuery("#hiddenFavLie").show();
                jQuery("#toutAfficher").hide();
                jQuery("#reduire").show();
        }
}
