    function selectMenuTab(selectedTabIdx) {
        $(".tron_menu a").removeClass("current");
        $(".tron_menu li").eq(selectedTabIdx).children("a").addClass("current");
    }

    var path = window.location.pathname;
    if (typeof(acInfos.entities.movie) != "undefined") {
        if (acInfos.entities.movie.cMovie == 3996)
            selectMenuTab(0);
        if (acInfos.entities.movie.cMovie == 29233 && window.location.pathname != '/evenement/tron/bof.aspx')
            selectMenuTab(1);
