<?php  class Mpersonne {	public static function acteurParId($id){			$bd = new Mconnect();		$connect=$bd->getRessource();		$stmt = ociparse($connect,"select p.ID_PERS, p.NOM_PERS, p.PRENOM_PERS									from PERSONNE p, JOUER j									WHERE p.ID_PERS=j.ID_PERS									AND j.ID_FILM='".$id."'"); 		ociexecute($stmt,OCI_DEFAULT); 				$tab = Mutilitaire::tableau($stmt);		return $tab;		$bd->close();	}		public static function realisateurParId($id){			$bd = new Mconnect();		$connect=$bd->getRessource();		$stmt = ociparse($connect,"select p.ID_PERS, p.NOM_PERS, p.PRENOM_PERS									from PERSONNE p, REALISER r									WHERE p.ID_PERS=r.ID_PERS									AND r.ID_FILM='".$id."'"); 		ociexecute($stmt,OCI_DEFAULT); 				$tab = Mutilitaire::tableau($stmt);		return $tab;		$bd->close();	}}?>